void testBignumIntConversion() {
    int64_t test_vars[] = {
            1, 100, 10000, 8924983, -32, 0, 8888888888888888888, -13232
    };

    for (int i = 0; i <= 6; ++i) {
        struct bignum b = bignumFromInt64(test_vars[i]);
        int64_t result = int64FromBignum(&b);

        printf("%ld -> %ld\n", test_vars[i], result);
        assert(result == test_vars[i]);
        free(b.data);
    }
}

void testShl() {
    int64_t input[] = {
            1, 1, 2, 3, 9, 121, 99, 123,
    };
    int64_t shl[] = {
            8, 2, 3, 4, 3, 9, 12, 33,
    };

    for (int i = 0; i < 8; ++i) {
        struct bignum b = bignumFromInt64(input[i]);
        shiftLeftInPlace(&b, shl[i]);

        int64_t result = int64FromBignum(&b);
        printf("Term: %ld << %ld | Expect: %ld ; Result %ld\n", input[i], shl[i], input[i] << shl[i], result);


        free(b.data);
        assert(result == input[i] << shl[i]);
    }

    char *inp[] = {
            "1A527C18D2B3D698C82FEE91461FF771",
            "16C4192B1D1043FEAEB6F0993D9A37043",
            "A"
    };
    uint64_t sh[] = {
            0x80,
            0x40,
            0x320
    };

    for (int i = 0; i < 3; ++i) {
        struct bignum fh = fromHex(inp[i]);

        shiftLeftInPlace(&fh, sh[i]);

        char *hx = toHex(&fh);
        printf("0x%s << 0x%lx == 0x%s # shift\n ", inp[i], sh[i], hx);

        bignumFree(fh);
        free(hx);
    }
}

void testShr() {
    int64_t input[] = {
            3232423, 83247, 256, 10, 9, 121, 99, 123,
    };
    int64_t shr[] = {
            3, 8, 2, 12, 3, 9, 12, 33,
    };

    for (int i = 0; i < 8; ++i) {
        struct bignum b = bignumFromInt64(input[i]);
        shiftRightInPlace(&b, shr[i]);

        int64_t result = int64FromBignum(&b);
        printf("Term: %ld >> %ld | Expect: %ld ; Result %ld\n", input[i], shr[i], input[i] >> shr[i], result);
        assert(result == input[i] >> shr[i]);

        bignumFree(b);
    }
}

void testAdd() {
    int64_t inputA[] = {
            1, 43, 1324, 124, 54, 9, 33, 199, 1, 43, 43
    };
    int64_t inputB[] = {
            1, 232, -123, 213, 1, 55, -122, 44, 1, 232, -232
    };

    for (int i = 0; i < 11; ++i) {
        struct bignum a = bignumFromInt64(inputA[i]);
        struct bignum b = bignumFromInt64(inputB[i]);

        struct bignum res = add(a, b);

        int64_t result = int64FromBignum(&res);

        printf("Term: %ld + %ld | Expect: %ld ; Result %ld\n", inputA[i], inputB[i], inputA[i] + inputB[i], result);
        assert(inputA[i] + inputB[i] == result);

        bignumFree(res);
        bignumFree(a);
        bignumFree(b);
    }

    char *inpS[] = {
            "3586C0035E11B129E63E097180B6F17C",

    };
    char *inpS2[] = {
            "110D4EC745A66559CD60EEB39F0C7638",

    };
    char *inpExp[] = {
            "46940ECAA3B81683B39EF8251FC367B4",
    };

    for (int i = 0; i < 1; ++i) {
        struct bignum a = fromHex(inpS[i]);
        struct bignum b = fromHex(inpS2[i]);

        struct bignum r = add(a, b);

        char *hex = toHex(&r);

        assert(strcmp(inpExp[i], hex) == 0);

        bignumFree(a);
        bignumFree(b);
        bignumFree(r);

        free(hex);
    }
}

void testNeg() {
    int64_t test[] = {
            232, 32432, 1231, 12, -122, 33, -1231223, -1,
    };

    for (int i = 0; i < 8; ++i) {
        struct bignum s = bignumFromInt64(test[i]);

        struct bignum ne = slowNegation(s);
        fastNegation(s);

        uint64_t fi = int64FromBignum(&s);
        uint64_t sl = int64FromBignum(&ne);

        uint64_t negated = -test[i];

        printf("Term: -%ld | Expect: %ld ; Result (slow) %ld and (fast) %ld\n", test[i], negated, sl, fi);

        assert(fi == negated);
        assert(sl == negated);

        bignumFree(s);
        bignumFree(ne);
    }
}

void testSub() {
    //TESTING FOR SUBTRACTION2() SWAP ALL SUBTRACTION2() Calls to SUBTRACTION1()
    int64_t inputA[] = {
            43, 1, 1324, 124, 54, 9, 33, 199,
    };
    int64_t inputB[] = {
            232, -1, -123, 213, 1, 55, -122, 44,
    };


    for (int i = 0; i < 8; ++i) {
        struct bignum a = bignumFromInt64(inputA[i]);
        struct bignum b = bignumFromInt64(inputB[i]);

        struct bignum res = subtraction(&a, &b);

        int64_t result = int64FromBignum(&res);

        printf("Term: %lx - %lx | Expect: %lx ; Result %lx\n", inputA[i], inputB[i], inputA[i] - inputB[i], result);
        assert(inputA[i] - inputB[i] == result);

        bignumFree(a);
        bignumFree(b);
        bignumFree(res);
    }
}

