void testFirstGuess() {
    int64_t inputA[] = {
            2, 4, 8, 16, 32
    };


    for (int i = 0; i < 5; i++) {
        struct bignum a = bignumFromInt64(inputA[i]);
        int s = getBitLength(&a);
        int shiftfactor = 32 - s;
        shiftLeftInPlace(&a, shiftfactor);
        struct bignum res = computeFirstGuess(&a, 32);


        char *str = toHex(&res);

        printf("For %u the reciprocal is %s\n", i, str);
        sleep(1);
        for (size_t j = 0; j < res.size; j++) {
            printf("value at index %zu is %u \n", j, res.data[j]);
        }


    }
}


void testFromToHex() {
    char *test_vars[] = {
            "E15F42BEBF140CE722C2291F89AE0F72799896A611E0E9CE2243F28",
            "1A527C18D2B3D698C82FEE91461FF77100000000000000000000000000000000",
            "1BE4DCFE998075DC",
            "998075DC",
            "BF56D3A3",
            "5FDE8B59",
            "D01DC696",
            "3F9BFEE81F6BF0A8CC753563D77CCF8DAE948F72F6B4287372E512E",
            "E9C60BA2F894139F810FD3C143C92C4DA981EED62330FBD2E9676BD",
            "DCA3DCED70FD3B9BC09BDE183AB2EF34E07C677F7220493A035C029",
            "6FC3ACD3A0647BB33F0448B4B84A3AED43306E545E9D267797722F8",
            "",
            "",
            "",
            "",
            "",
            "186940ecaa3b81683b39ef8251fc367b4",
    };

    for (int i = 0; i < 16; ++i) {
        struct bignum x = fromHex(test_vars[i]);
        char *resu = toHex(&x);

        assert(strcmp(test_vars[i], resu) == 0);

        free(x.data);
        free(resu);
    }
}

void testHex() {
    uint64_t test_vars[] = {
            329048023,
            19032,
            1923412093,
            1,
            0,
            2,
            9999,
            1
    };

    for (int i = 0; i < 8; ++i) {
        struct bignum x = bignumFromInt64(test_vars[i]);
        char *resu = toHex(&x);


        printf("0x%lx == 0x%s\n", test_vars[i], resu);
        //assert(strcmp(test_vars[i], resu) == 0);

        free(x.data);
        free(resu);
    }
}


void printHeader(char *title) {
    printf("\n");
    printf("--- Tests: %s ---\n", title);
    printf("\n");
}

void testGetInt() {
    char *test[] = {
            "500",
            "45",
            "h900",
            "700"
    };

    for (size_t i = 0; i < sizeof(test) / sizeof(test[0]); i++) {
        char *curr = test[i];
        size_t s = getint(curr);

        printf("the result is %zu\n", s);
    }
}

void testGoldschmidt() {
    int64_t inputA[] = {
            5, 3, 8, 2300403898, 4
    };
    int64_t inputB[] = {
            4, 2, 5, 5553666290, 3
    };
    //sqrt(2) - 1 = 0.41421356237
    char *results[] = {
            "5368709120", "6442450944", "6871947673", "0.5153960755", "5726623061"
    };

    char *reshex32[] = {
            "140000000", "180000000", "199999999", "6A09E668", "0x155555555"
    };


    int bindecimalplaces = 1000;

    for (int i = 0; i < 5; ++i) {
        struct bignum a = bignumFromInt64(inputA[i]);
        struct bignum b = bignumFromInt64(inputB[i]);


        struct bignum res = goldschmidtDiv(&a, &b, bindecimalplaces);

        char *res2 = toHex(&res);


        printf("Term: %ld / %ld | Expect(Decimal): %s, Expect(Hex) %s ; Result: %s for %u decimal places\n",
               inputA[i], inputB[i], results[i], reshex32[i], res2, bindecimalplaces);


        //assert(inputA[i] / inputB[i] == result);

        free(res2);
    }
}


